import React from 'react';
import {Switch,Route} from 'react-router-dom';
import Register from './components/Register'
import Home from './components/Home'
import Login from './components/login';
import SignalPost from './components/Signal_post'
import Header from './components/Header'
import Footer from './components/Footer';
import UploadPost from './components/UploadPost'
import Timeline from './components/Timeline';
import Forget from './components/Footer';
import Reset from './components/Reset';
class App extends React.Component{
  constructor(props){
    super(props)
    this.state={
      clickEvent:true,
      clickEventHome:false,
      componentName:'register',
      showTimeline:false, 
      email:''

    }

  }

  // setEmail=(emailData)=>{
  //   this.setState({email:this.state.email});
  // }



  handleClick = (event,booleanValue) =>{
      this.setState({clickEvent:this.state.clickEvent?false:true});
      this.setState({  componentName : event,
                      showTimeline:booleanValue,
                      email:this.state.email})

  }


  handleClickHome = (event) =>{
    this.setState({clickEventHome:true,email:event});


}

showComponent =(component)=>{ 
  if(this.state.componentName==='register'){ 
       return  <Register  onClick={this.handleClick} componentChange={this.handleClick}  />
  }else if(this.state.componentName==='login'){
        return <Login  onClick={this.handleClick} componentChange={this.handleClick}  onClickHome={this.handleClickHome}  />
  }else if(this.state.componentName==='home'){
       return   <Home  componentChange={this.handleClick}  />
  }else if(this.state.componentName==='timeline'){
       return <Timeline/>
  }else if(this.state.componentName==='upload'){
       return <UploadPost email={this.state.email}  emailSent={this.state.email}  />
  }

}


  render(){

    return( 

      <div>
        {
          /* { this.state.clickEventHome?
           <Home /> :this.state.clickEvent?<Register onClick={this.handleClick} />:<Login onClick={this.handleClick}   onClickHome={this.handleClickHome}/>}
       */
         } 
       <Header componentProp={this.state.showTimeline}    onClick={this.handleClick}  />
           {/* {this.showComponent(this.state.componentName)} */}
           <Switch>
        <Route exact path='/' component={<Register/>} />
            <Route path='/login' component={Login}/>
            <Route path='/timeline' component={Timeline}/>
            <Route path='/forget' component={Forget}/>
            <Route path='/Reset' component={Reset}/>
            <Route path='/home' component={Home}/>
            <Route path='/uploadpost' component={UploadPost}/>
          </Switch>
       <Footer/>
      </div>
    )
  }
}

export default App;
