import React from 'react'
import axios from 'axios';
export  default class UploadPost extends React.Component{
    constructor(props){
        super(props)
        this.state={
            email:'',
            title:'',
            category:'Cats',
            pic:''
        }
    }

    handleInput=(e)=>{

        this.setState({[e.target.name]:e.target.value});

    }

    componentDidMount(){

        this.setState({email:this.props.emailSent})

    }

    handleSubmit=(e)=>{
        e.preventDefault();
        const data=this.state;
        //const arr=data.pic.split('\\')
        //data.pic=arr[arr.length-1];

        console.log('data uploaded',data)
        axios.post('http://localhost:8086/addpost',  data,{ headers: { 'Content-Type': 'multipart/form-data' } } )
        .then((data) => { 

            console.log('data returned after uploading',data);


  });


    }


    render(){
        return(
            <div >
                <link href="css/style2.css" rel="stylesheet" type="text/css" />
                 <form   id='form'  onSubmit={this.handleSubmit} >
                 <div><span style={{fontSize:30}} >UPLOAD WEB DATA </span></div>
                 <div><span>Title</span></div>
                 <input type="text" name='title'  value={this.state.title} onChange={this.handleInput}/>
                 <div><span>Category</span></div>
                 <select name="category"  value={this.state.value}  onChange={this.handleInput}>
                     <option value="Cats">Cats</option>
                     <option value="Dogs">Dogs</option>
                     <option value="Birds">Birds</option>
                     <option value="Rabbits">Rabbits</option>
                     <option value="Others">Others</option>
                 </select>
                 <div><span>Choose image</span></div>
                 <input type="file" name='pic' onChange={this.handleInput} />
                  <input type='submit' value="Upload"/>
                </form> 
            </div>
        );
    }
}
