import React from 'react'
export  default class UploadPost extends React.Component{
    constructor(props){
        super(props)
        this.state={
            title:'',
            category:'',
            pic:''
        }
    }

    handleInput=(e)=>{

        this.setState({[e.target.name]:e.target.value});

    }

    handleSubmit=(e)=>{
        e.preventDefault();
        console.log('data',this.state)
    }


    render(){
        return(
            <div >
                <link href="css/style2.css" rel="stylesheet" type="text/css" />
                 <form   id='form'  onSubmit={this.handleSubmit} >
                 <div><span style={{fontSize:30}} >UPLOAD WEB DATA </span></div>
                 <div><span>Title</span></div>
                 <input type="text" name='title'  value={this.state.title} onChange={this.handleInput}/>
                 <div><span>Category</span></div>
                 <select name="category"  onSelect={this.handleInput}>
                     <option value={this.state.category}>Cats</option>
                     <option value={this.state.category}>Dogs</option>
                     <option value={this.state.category}>Birds</option>
                     <option value={this.state.category}>Rabbits</option>
                     <option value={this.state.category}>Others</option>
                 </select>
                 <div><span>Choose image</span></div>
                 <input type="file" name='pic' onChange={this.handleInput} />
                  <input type='submit' value="Upload"/>
                </form> 
            </div>
        );
    }
}
