import React from 'react';
import Register from './components/Register'
import Home from './components/Home'
import Login from './components/login';
import SignalPost from './components/Signal_post'
import Header from './components/Header'
import Footer from './components/Footer';
import UploadPost from './components/UploadPost'
import Timeline from './components/Timeline';
import Forget from './components/Footer';
import Reset from './components/Reset';
import PrivateRoute from 'react-private-route';
import { 
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
  useParams,
  useRouteMatch
} from "react-router-dom";

class App extends React.Component{
  constructor(props){
    super(props)
    this.state={
      clickEvent:true,
      clickEventHome:false,
      showTimeline:false, 
      email:'',

    }

  }
  handleClickHome = (event) =>{
    this.setState({clickEventHome:true,email:event}); 
  }

  render(){

    return( 
      <div>

             <Header  />
           <Switch> 
                <Route path='/uploadpost'  component={UploadPost} /> 
                <Route path='/timeline'  component={Timeline} />
                <Route path='/home'  component={Home} />
                <Route path='/login' render={(props) => <Dashboard {...props} isAuthed={this.handleClickHome} />}  />
                <Route  exact path='/'  component={Register} />


          </Switch>

       <Footer/>

      </div>
    )
  }
}

export default App;

