import React from 'react'
import axios from 'axios';
export default class Posts extends React.Component{


  constructor(props){
    super(props);
    this.state={
      postsData:[],
    }
  }

    componentDidMount(){

    //   console.log('timeline email','sdfsd@gmial.com')
      const email={email:'sdfsd@gmial.com'}
      axios.post('http://localhost:8086/posts',email)
         .then((data) => { 
            if(data){
               // this.props.onClick('home',true);
               // this.props.onClickHome(this.state.email);

               console.log('timeline',data.data[0]);
               this.setState({postsData:data.data});

            }else{    
                this.setState({conditionalcss:true,logInMessage:'Wrong password'})
            }

   });

    }



    makePosts=()=>{
       let array=this.state.postsData
       let newArray=array;

       for( let i of array){
               if(i.category===this.props.category){
                console.log('value of i',i)
                newArray.push(i)
               }

       }

        const posts=newArray.map( (array)=>{

         //   if(array.category===this.state.category){
        return(
          <div className="contnt_2">
          <div className="div_a">
            <div className="div_title">{array.title}</div>
            <div className="btm_rgt">
              <div className="btm_arc">{array.category}</div>
            </div>
            <div className="div_top">
              <div className="div_top_lft"><img src="images/img_6.png" />{array.email}</div>
              <div className="div_top_rgt"><span className="span_date">{new Date(array.date).toLocaleDateString()}</span><span className="span_time">{new Date(array.date).toLocaleTimeString()}</span></div>
            </div>
            <div className="div_image"><img  src={`http://localhost:8086/${array.pic}`} alt="pet" /></div>
            <div className="div_btm">
              <div className="btm_list">
                <ul>
                  <li><a href="#"><span className="btn_icon"><img src="images/icon_001.png" alt="share" /></span>Share</a></li>
                  <li><a href="#"><span className="btn_icon"><img src="images/icon_002.png" alt="share" /></span>Flag</a></li>
                  <li><a href="#"><span className="btn_icon"><img src="images/icon_003.png" alt="share" /></span>0 Likes</a></li>
                  <li><a href="#"><span className="btn_icon"><img src="images/icon_004.png" alt="share" /></span>4 Comments</a></li>
                </ul>
              </div>
            </div>
          </div>
        </div>);

        // }else{
        //     return null;
        // }


        }


        );



        return posts;

      }

      render(){
          return(
              <div>
                  {this.makePosts()}
              </div>
          );
      }

    }
