import React from 'react';
import axios from 'axios';
import { exists } from 'fs';
import Cookies from 'js-cookie'
import {Link} from 'react-router-dom';
import { Redirect } from 'react-router-dom';

class Login extends React.Component{
    constructor(props){
        super(props);
        this.state={
            email:'',
            password:'',
            conditionalcss:false,
            logInMessage:'',
            check:false,
            authUser:false,
        }     
    }
    componentDidMount(){
       this.setState({email:Cookies.get('email_ppl'),password:Cookies.get('password_ppl')});
       if(Cookies.get('email_ppl')!=='' && Cookies.get('password_ppl')!==''){
        document.getElementById('remeber').checked=true;
       }
    }



    handleUserInput=(event)=>{

        let nameDefined=event.target.name;
        this.setState({[nameDefined]:event.target.value,conditionalcss:false,logInMessage:''});
        document.getElementById('remeber').checked=false;

      }

        handleSubmit=(event)=>{

                const user =this.state;
                 axios.post('http://localhost:8086/login',  user )
                    .then((data) => {   

                       if(data.data===true){
                        this.setState({authUser:true});
                       }else{    
                           this.setState({conditionalcss:true,logInMessage:'Wrong password'})
                       }

              });
              event.preventDefault(); 

          }



          handleCheckClick=(event)=>{
            if(event.target.checked){
              Cookies.set('email_ppl',this.state.email , { expires: 17777 },) 
              Cookies.set('password_ppl', this.state.password, { expires: 17777 }) 

            }else{
              Cookies.set('email_ppl', '', { expires: 17777}); 
              Cookies.set('password_ppl', '', { expires: 17777 }); 
            }
          }

    render(){

  {console.log(this.state.check)}
        return (
            <div>
              <meta charSet="utf-8" />
              <title>Login Account</title>
              <link href="css/bootstrap.css" rel="stylesheet" type="text/css" />
              <link href="css/bootstrap-responsive.css" rel="stylesheet" type="text/css" />
              <div className="container">
                <div className="content">
                  <div className="content_rgt">
                    <div className="login_sec">
                      <h1>Log In</h1>

                          <form onSubmit={this.handleSubmit} autoComplete="off" >   
                          <ul>       
                        <li><span>Email-ID</span><input type="email"  style={this.state.conditionalcss ? {color:'red'} : {color:'black'} }  value={this.state.email} name='email' onChange={this.handleUserInput}  placeholder="Enter your email" required /></li>
                        <li><span>Password</span><input type="password" onChange={this.handleUserInput} name='password'value={this.state.password} name='password'  placeholder="Enter your password"  required/></li>
                        <li><input type="checkbox" id='remeber' onClick={this.handleCheckClick} />Remember Me</li>
                        <li><input type="submit" defaultValue="Log In"  />
                        <a href="#">Forgot Password</a></li>
                        <div name='user_create'  style={{color:'red'}}>{this.state.logInMessage}</div>
                        {this.state.authUser?<Redirect to={"/home?email:"+this.state.email} />:null }
                      </ul>
                      </form>

                      <div className="addtnal_acnt">I do not have any account yet.<Link to='/' onClick={this.handleClick} >Create My Account Now !</Link></div>
                    </div>
                  </div>
                  <div className="content_lft">
                    <h1>Welcome from PPL!</h1>
                    <p className="discrptn">There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum, you need to be sure there isn't anything embarrassing hidden in the middle of text. </p>
                    <img src="images/img_9.png" alt="" /> 
                  </div>
                </div>
              </div>
              <div className="clear" />

            {this.state.check ?  <Redirect to='/home'/> : console.log('inside',this.state.check)}
            </div>
          );
    }


   }

   export default Login;
